import React, { useContext } from 'react';
import { PokemonContext } from '../context/PokemonContext';
import { Loader } from './Loader';
import { Link } from 'react-router-dom';

export const PokemonList = () => {
  const { allPokemons, loading, filteredPokemons } = useContext(PokemonContext);
  const renderPokemon = (pokemon) => (
	<Link to={`/pokemon/${pokemon.id}`} className='card-pokemon' key={pokemon.id}>
	  <div className='card-img'>
		<img
		  src={pokemon.sprites?.other?.dream_world?.front_default}
		  alt={`Pokemon ${pokemon.name}`}
		/>
	  </div>
	  <div className='card-info'>
		<span className='pokemon-id'>NÂ° {pokemon.id}</span>
		<h3>{pokemon.name}</h3>
		<div className='card-types'>
		  {pokemon.types.map((type) => (
			<span key={type.type.name} className={type.type.name}>
			  {type.type.name}
			</span>
		  ))}
		</div>
	  </div>
	</Link>
  );
  return (
    <>
      {loading ? (
        <Loader />
      ) : (
        <div className='card-list-pokemon container'>
          {filteredPokemons.length ? (
            <>
              {filteredPokemons.map((pokemon) => (
                <React.Fragment key={pokemon.id}>{renderPokemon(pokemon)}</React.Fragment>
              ))}
            </>
          ) : (
            <>
              {allPokemons.map((pokemon) => (
                <React.Fragment key={pokemon.id}>{renderPokemon(pokemon)}</React.Fragment>
              ))}
            </>
          )}
        </div>
      )}
    </>
  );
};
